export const newID = () => {
    let timestamp = (new Date().getTime() / 1000 | 0).toString(16);
    return timestamp + 'xxxxxxxxxxxxxxxx'.replace(/[x]/g, () => {
        return (Math.random() * 16 | 0).toString(16);
    }).toLowerCase();
};
export const isObject = (x) => (typeof x === 'object' && x !== null && !isArray(x)) ? true : false;
export const isArray = (x) => Array.isArray(x);
export const _condi = (field, document, conditions) => {
    if (isObject(conditions)) {
        let valid = true;
        for (let condi in conditions) {
            switch (condi) {
                case '$gte':
                    valid = (document[field] >= conditions[condi] ? true : false);
                    break;
                case '$gt':
                    valid = (document[field] > conditions[condi] ? true : false);
                    break;
                case '$lt':
                    valid = (document[field] < conditions[condi] ? true : false);
                    break;
                case '$lte':
                    valid = (document[field] <= conditions[condi] ? true : false);
                    break;
                default:
                    throw `Unkown operator ${condi}`;
            }
            if (valid === false)
                return valid;
        }
        return valid;
    }
    return (document[field] === conditions ? true : false);
};
